mapper.select:
SELECT
	COUNT(*) CNT
FROM ks_board_activity
WHERE BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
AND USE_YN = 'Y'

SELECT B.BOARD_NO , B.BOARD_TYPE , B.BOARD_SUB_TYPE , B.BOARD_DATE , B.TITLE , B.CONTENT , B.HIT , B.REG_ID , B.REG_DTTM , FILE_NO , FILE_NM , FILE_DIR FROM ks_board_activity B LEFT JOIN KS_FILE_activity F ON B.BOARD_NO = F.BOARD_NO AND B.BOARD_TYPE = F.BOARD_TYPE AND B.BOARD_SUB_TYPE = F.BOARD_SUB_TYPE AND F.USE_YN = 'Y' WHERE B.USE_YN = 'Y' AND B.BOARD_TYPE = #{boardType} AND B.BOARD_SUB_TYPE = #{boardSubType}
AND B.BOARD_DATE = #{boardDate}
ORDER BY REG_DTTM DESC LIMIT 1

mapper.update:
UPDATE ks_board_activity SET
	HIT = IFNULL(HIT, 0) + 1
WHERE BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
AND USE_YN = 'Y'

mapper.insert:
	
    	select DATE_FORMAT(NOW(), '%Y%m%d') AS BOARD_DATE FROM DUAL
	
	INSERT INTO ks_board_activity(
		BOARD_TYPE
		, BOARD_SUB_TYPE
		, BOARD_DATE
		, TITLE
		, CONTENT
		, HIT
		, USE_YN
		, REG_ID
	) VALUES (
		#{boardType}
		, #{boardSubType}
		, #{boardDate}
		, #{title}
		, #{content}
		, 0
		, 'Y'
		, #{regId}
	)

mapper.update:
UPDATE ks_board_activity SET
	CONTENT = #{content}
	, MOD_ID = #{modId} 
	, MOD_DTTM = NOW()
WHERE BOARD_TYPE = #{boardType}
	AND BOARD_SUB_TYPE = #{boardSubType}
	AND BOARD_DATE = #{boardDate}

mapper.insert:
INSERT INTO ks_board_activity_reply(
	BOARD_NO
	, BOARD_TYPE
	, BOARD_SUB_TYPE
	, REPLY_CONTENT
	, USE_YN
	, REG_ID
) VALUES (
	#{boardNo}
	, #{boardType}
	, #{boardSubType}
	, #{replyContent}
	, 'Y'
	, #{replyRegId}
)

mapper.update:
UPDATE ks_board_activity_reply SET
	BOARD_NO = #{boardNo}
WHERE BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
