mapper.select:
SELECT
	*
FROM ks_board_activity
WHERE BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
AND USE_YN = 'Y'

SELECT
	COUNT(*) CNT
FROM ks_board_activity
WHERE BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
AND USE_YN = 'Y'

mapper.update:
UPDATE ks_board_activity SET
	HIT = IFNULL(HIT, 0) + 1
WHERE BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
AND USE_YN = 'Y'

mapper.insert:
INSERT INTO ks_board_activity(
	BOARD_TYPE
	, BOARD_SUB_TYPE
	, TITLE
	, CONTENT
	, HIT
	, USE_YN
	, REG_ID
) VALUES (
	#{boardType}
	, #{boardSubType}
	, #{title}
	, #{content}
	, 0
	, 'Y'
	, #{regId}
)

INSERT INTO ks_board_activity_reply(
	BOARD_NO
	, BOARD_TYPE
	, BOARD_SUB_TYPE
	, REPLY_CONTENT
	, USE_YN
	, REG_ID
) VALUES (
	#{boardNo}
	, #{boardType}
	, #{boardSubType}
	, #{replyContent}
	, 'Y'
	, #{replyRegId}
)
