mapper.select:
SELECT
	*
FROM ks_file
WHERE FILE_NO = #{fileNo}
AND BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}

mapper.insert:
INSERT INTO ks_file(
	BOARD_TYPE
	, BOARD_NO
	, FILE_SEQ
	, FILE_NM
	, FILE_EXT
	, FILE_DIR
	, REG_ID
) VALUES (
	#{boardType}
	, #{boardNo}
	, #{fileSeq}		
	, #{fileNm}		
	, #{fileExt}		
	, #{fileDir}		
	, #{regId}		
)

mapper.select:
SELECT
	*
FROM ks_file_activity
WHERE FILE_NO = #{fileNo}
AND BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.insert:
INSERT INTO ks_file_activity(
	BOARD_TYPE
	, BOARD_SUB_TYPE
	, BOARD_NO
	, FILE_NM
	, FILE_EXT
	, FILE_DIR
	, REG_ID
) VALUES (
	#{boardType}
	, #{boardSubType}
	, #{boardNo}
	, #{fileNm}		
	, #{fileExt}		
	, #{fileDir}		
	, #{regId}		
)

INSERT INTO ks_board_activity_video(
	BOARD_TYPE
	, BOARD_SUB_TYPE
	, VIDEO_URL
	, THUMBNAIL_URL
	, REG_ID
) VALUES (
	#{boardType}
	, #{boardSubType}
	, #{videoUrl}
	, #{thumbnailUrl}
	, #{regId}		
)

mapper.update:
UPDATE ks_board_activity_video SET
	VIDEO_URL = #{videoUrl}
	, THUMBNAIL_URL = #{thumbnailUrl}
	, MOD_ID = #{modId}
	, MOD_DTTM = NOW()
WHERE VIDEO_NO = #{videoNo}
AND BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}

mapper.select:
SELECT
	*
FROM ks_board_activity_video
WHERE BOARD_TYPE = #{boardType}
AND BOARD_SUB_TYPE = #{boardSubType}
