mapper.select:
SELECT
	*
FROM ks_file
WHERE FILE_NO = #{fileNo}
AND BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}

mapper.insert:
INSERT INTO ks_file(
	BOARD_TYPE
	, BOARD_NO
	, FILE_SEQ
	, FILE_NM
	, FILE_EXT
	, FILE_DIR
	, REG_ID
) VALUES (
	#{boardType}
	, #{boardNo}
	, #{fileSeq}		
	, #{fileNm}		
	, #{fileExt}		
	, #{fileDir}		
	, #{regId}		
)
