mapper.select:
SELECT
	*
FROM ks_board_forum
WHERE BOARD_TYPE = #{boardType}
AND USE_YN = 'Y'
limit #{length} offset #{start}

mapper.update:
UPDATE ks_board_forum SET
	HIT = IFNULL(HIT, 0) + 1
WHERE BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}
AND USE_YN = 'Y'

