mapper.select:
SELECT
	*
FROM ks_board_sinmungo
WHERE BOARD_TYPE = #{boardType}
AND USE_YN = 'Y'
limit #{length} offset #{start}

SELECT
	*
FROM ks_board_sinmungo
WHERE BOARD_NO = #{boardNo} 
AND BOARD_TYPE = #{boardType}
AND USE_YN = 'Y'

mapper.update:
UPDATE ks_board_sinmungo SET
	HIT = IFNULL(HIT, 0) + 1
WHERE BOARD_NO = #{boardNo}
AND BOARD_TYPE = #{boardType}
AND USE_YN = 'Y'

mapper.insert:
INSERT INTO ks_board_sinmungo(
	BOARD_TYPE
	, TITLE
	, CONTENT
	, HIT
	, USE_YN
	, REG_ID
) VALUES (
	#{boardType}
	, #{title}
	, #{content}
	, 0
	, 'Y'
	, #{regId}
)
